-- Name: csv_account_summary_vw; Type: VIEW; Schema: public; Owner: cynergiuser
--

CREATE VIEW public.csv_account_summary_vw AS
 SELECT 'corjim'::text AS dataset,
    stores.loc_tran_loc AS store_number,
    customers.cust_acct_nbr AS customer_number,
    customers.cust_first_name_mi AS first_name,
    customers.cust_last_name AS last_name,
    customers.cust_email_address AS email,
    agreements.agreement_number,
    agreement_versions.agreement_contract_date AS date_rented,
    agreement_versions.agreement_next_due_date AS due_date,
    round((((agreement_versions.agreement_contract_amt - agreement_versions.agreement_contract_balance) / NULLIF(agreement_versions.agreement_contract_amt, (0)::numeric)) * (100)::numeric), 2) AS percent_ownership,
    models.itemfile_desc_1 AS product,
    round((agreement_versions.agreement_contract_amt / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)), 0) AS terms,
    agreement_versions.agreement_payment_amt AS next_payment_amount,
    customers.cust_address AS address_1,
    customers.cust_address_2 AS address_2,
    customers.cust_city AS city,
    customers.cust_state AS state,
    customers.cust_zip_pc AS zip,
        CASE
            WHEN (ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric))) = (0)::numeric) THEN (1)::numeric
            ELSE ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)))
        END AS payments_remaining,
    ( SELECT (('now'::text)::date + ((
                CASE
                    WHEN ((agreement_versions.agreement_payment_terms)::text = 'M'::text) THEN round(trunc(((1.00 * agreement_versions.agreement_contract_balance) / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)), 2))
                    WHEN ((agreement_versions.agreement_payment_terms)::text = 'W'::text) THEN round((trunc(((1.00 * agreement_versions.agreement_contract_balance) / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)), 2) / 4.33))
                    WHEN ((agreement_versions.agreement_payment_terms)::text = 'B'::text) THEN round((trunc(((1.00 * agreement_versions.agreement_contract_balance) / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)), 2) / (2)::numeric))
                    ELSE round((trunc(((1.00 * agreement_versions.agreement_contract_balance) / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)), 2) / (2)::numeric))
                END)::integer * 30))) AS projected_payout_date,
    min(
        CASE
            WHEN (
            CASE
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'M'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) * 4.33))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'W'::text) THEN ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'B'::text) THEN ceil((((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric) * 4.33))
                ELSE ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) * (2)::numeric))
            END = (0)::numeric) THEN (1)::numeric
            ELSE
            CASE
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'M'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) * 4.33))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'W'::text) THEN ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'B'::text) THEN ceil((((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric) * 4.33))
                ELSE ceil((((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric) * 4.33))
            END
        END) AS weeks_remaining,
    min(
        CASE
            WHEN (
            CASE
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'M'::text) THEN ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'W'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / 4.33))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'B'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric))
                ELSE ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric))
            END = (0)::numeric) THEN (1)::numeric
            ELSE
            CASE
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'M'::text) THEN ceil((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'W'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / 4.33))
                WHEN ((agreement_versions.agreement_payment_terms)::text = 'B'::text) THEN ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric))
                ELSE ceil(((agreement_versions.agreement_contract_balance / NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric)) / (2)::numeric))
            END
        END) AS months_remaining,
        CASE
            WHEN (((agreement_versions.agreement_open_flag)::text = 'Y'::text) AND (agreement_versions.agreement_next_due_date < ('now'::text)::date)) THEN 'Y'::text
            ELSE 'N'::text
        END AS past_due,
        CASE
            WHEN (((agreement_versions.agreement_open_flag)::text = 'Y'::text) AND (agreement_versions.agreement_next_due_date < ('now'::text)::date)) THEN (('now'::text)::date - agreement_versions.agreement_next_due_date)
            ELSE NULL::integer
        END AS days_overdue,
    ((
        CASE
            WHEN (
            CASE
                WHEN (((agreement_versions.agreement_payment_terms)::text = 'M'::text) AND ((agreement_versions.agreement_open_flag)::text = 'Y'::text) AND (agreement_versions.agreement_next_due_date < ('now'::text)::date)) THEN (NULLIF(agreement_versions.agreement_payment_amt, (0)::numeric) *
